import { Core } from '../index'

describe('Core', () => {
  let core

  beforeEach(() => {
    core = new Core()
  })
  afterEach(() => {
    core.removeAllListeners()
    core.off('transport_open')
    // logMock.mockRestore()
  })
  it('should create a new instance', () => {
    expect(core).toBeInstanceOf(Core)
  })

  it('should initialize with correct default values', () => {
    expect(core.connected).toBe(false)
    expect(core.pending).toBe(false)
  })
})
