import BinanceW3WProvider from '../index'

describe('EthereumProvider', () => {
  it('should be able to create a new instance', () => {
    const provider = new BinanceW3WProvider()
    expect(provider).toBeInstanceOf(BinanceW3WProvider)
  })
  it('should be able to create a new instance with a custom rpc provider', () => {
    const provider = new BinanceW3WProvider({
      rpc: { 1: 'https://mainnet.infura.io/v3/123' },
    })
    expect(provider).toBeInstanceOf(BinanceW3WProvider)
  })
})
